// Read http://www.tinaja.com/glib/nonlingr.pdf
// nonlinear transforms

THREE.Path.prototype.nltransform = function( a, b, c, d, e, f ) {

  // a - horizontal size
  // b - lean
  // c - x offset
  // d - vertical size
  // e - climb
  // f - y offset

  var oldPts = this.getPoints();

  var i, il, p, oldX, oldY;

  for ( i = 0, il = oldPts.length; i < il; i ++ ) {

    p = oldPts[i];

    oldX = p.x;
    oldY = p.y;

    p.x = a * oldX + b * oldY + c;
    p.y = d * oldY + e * oldX + f;

  }

  return oldPts;

};


